import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";


function Home() {
  const [getUserData,setGetUserData]=useState([])
  console.log(getUserData)

  async function getData(){
    const res= await fetch("/getdata",{
       method:"GET",
       headers:{
          "content-type":"application/json"
       },
    })

    const myData= await res.json()
    // console.log(myData)
    if(res.status===400 || res.status===404 || !myData){
       alert("error")
       console.log("something error")
    }
    else{
       setGetUserData(myData.data)
      //  console.log("data get successfully")
    }  
}

useEffect(()=>{
  getData()
},[])
  return (
    <>
      <div className="container mt-4">
        <div className="my-2 text-end">
          <Link to="/adduser" className="btn btn-primary"><i class="fa-solid fa-plus"></i> Add Data</Link>
        </div>
        <div className="table-responsive">
        <table className="table text-center table-responsive">
          <thead>
            <tr className="table-dark">
              <th scope="col">Id</th>
              <th scope="col">Username</th>
              <th scope="col">Email</th>
              <th scope="col">Job</th>
              <th scope="col">Number</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
            {
              map.getUserData((item,index)=>{
                return (
                  <tr>
                    <th scope="row"></th>
                    <td>Alok</td>
                    <td>alok@gmail.com</td>
                    <td>Web Developer</td>
                    <td>6392190896</td>
                    <td className="d-flex justify-content-around gap-1">
                        <button className="btn btn-success"><i className="fa-solid fa-eye"></i></button>
                        <button className="btn btn-primary"><i className="fa-solid fa-pen-to-square"></i></button>
                        <button className="btn btn-danger"><i className="fa-solid fa-trash"></i></button>
                    </td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
        </div>
      </div>
    </>
  );
}

export default Home;
